mapper.select:
select * from board b join member on (b.board_writer = member_id) join community c on (b.board_no = c.board_no) where b.b_status = 'Y' and b.board_type = '2'
and c.commu_type = #{commuType}
order by b.create_date desc

mapper.update:
update board
set board_count = board_count + 1
where board_no = #{bNo}

mapper.select:
select *
from IMG_REPOSITORY
where IMG_STATUS = 'Y' and IMG_B_TYPE = '2' and img_key = #{bNo}

mapper.update:
update community
set SYMPT_COUNT = SYMPT_COUNT + 1
where board_no = #{bNo}

mapper.select:
select *
from wishList
where MEMBER_ID = #{memberId} and BOARD_NO = #{boardNo}

mapper.update:
update community
set SYMPT_COUNT = SYMPT_COUNT - 1
where board_no = #{bNo}

update REPLY
set REPLY_STATUS = 'N'
where REPLY_NO = #{rNo}

mapper.delete:
delete from IMG_REPOSITORY
where IMG_SERVER_NAME in

	#{item}

mapper.update:
update community
set commu_type = #{commuType}
where board_no = #{boardNo}

update board
set board_title = #{boardTitle}, board_content = #{boardContent}, MODIFY_DATE = sysdate, board_type = #{boardType}
where board_no = #{boardNo}

update board
set B_STATUS = 'N'
where board_no = #{bNo}

update IMG_REPOSITORY
set IMG_STATUS = 'N'
where IMG_KEY = #{strBNo}
