mapper.update:
update board
set board_count = board_count + 1
where board_no = #{bNo}

mapper.select:
select * from BOARD b join market m on(m.board_no = b.board_no) join member on(board_writer = member_id) where BOARD_TYPE = '3' and B_STATUS = 'Y'
and m.MARKET_TYPE = #{marketType}
order by b.BOARD_NO desc

select *
from wishList
where MEMBER_ID = #{memberId} and BOARD_NO = #{boardNo}

mapper.update:
update board
set B_STATUS = 'N'
where BOARD_NO = #{bNo}

update IMG_REPOSITORY
set IMG_STATUS = 'N'
where IMG_KEY = #{strBNo}
