mapper.select:
select count(*)
from member
where member_id = #{memberId}

select count(*)
from member
where nickname = #{nickName}

mapper.update:
update member
set member_pwd = #{memberPwd}
where email = #{email}
	and member_id = #{memberId}
	and status = 'Y'

mapper.select:
select count(*)
from IMG_REPOSITORY
where img_key = #{memberId}

mapper.update:
update img_repository
set img_name = #{imgOriginalName}, IMG_SERVER_NAME = #{imgRename}, IMG_MODIFY_DATE = sysdate, IMG_PATH = #{imgPath}
where img_key =  #{imgKey}

mapper.select:
select *
from img_repository
where img_key = #{memberId}

mapper.update:
update member
set status = 'N'
where member_id = #{memberId}

mapper.select:
select count(*)
from wishlist
where board_no = #{boardNo}

select count(*) from product_inquiry where inquiry_status = 'Y' and user_id = #{memberId}
and admin_status = 'N'
and admin_status = 'Y'

select * from product_inquiry where inquiry_status = 'Y' and user_id = #{memberId}
and admin_status = 'N'
and admin_status = 'Y'
