mapper.select:
select count(*)
from member
where member_id = #{memberId}

select count(*)
from member
where nickname = #{nickName}

select member_id
from member
where member_name = #{memberName} and email = #{email}

select count(*)
from member
where member_id = #{memberId} and email = #{email}

mapper.update:
update member
set member_pwd = #{memberPwd}
where email = #{email}
	and member_id = #{memberId}
	and status = 'Y'
